<style lang="postcss">
	.case {
		position: relative;
		background: black;
		padding: 4px 4px 0 4px;
		height: auto;
		border-radius: 10px 10px 30px 30px;
		background: linear-gradient(180deg, #000, #111);
	}

	.case-glass {
		border-radius: 10px 10px 30px 30px;
		position: absolute;
		top: 0;
		width: 100%;
		height: 95%;
		background: linear-gradient(to right, #ffffff33, #ffffff66);
		z-index: 10;
		left: 0;
		box-shadow:
			inset 0 0 0px 4px #111,
			inset 0 0 0px 10px #222;
		border: solid 2px #222;
	}

	.case-lid {
		border-radius: 10px 10px 20px 20px;
		position: absolute;
		top: 0;
		width: 100%;
		border-top: solid 2px #222;
		border-right: solid 2px #222;
		border-left: solid 2px #111;
		border-bottom: solid 2px #111;
		height: 60%;
		background: radial-gradient(circle at center, #11111133, #00000033),
			linear-gradient(140deg, #111, #333);
		z-index: 11;
		left: 0;
	}

	.disc {
		position: relative;
		z-index: 9;
		width: 70px;
		height: auto;
		left: 6px;
	}

	.spinning {
		animation: spin 0.5s linear infinite forwards;
	}

	.sic-man {
		position: absolute;
		left: 50%;
		height: auto;
		translate: -50% 0 0;
		top: 30%;
		z-index: 11;
		width: 30px;
		opacity: 0.7;
	}

	.shadow {
		background: #000;
		position: absolute;
		bottom: 0;
		left: 50%;
		translate: -50% 0 0;
		width: 50%;
		height: 30%;
		opacity: 0.5;
		box-shadow: 0 0 26px 10px rgba(0, 0, 0, 1);
	}

	@keyframes spin {
		0% {
			transform: rotate(0deg);
		}
		100% {
			transform: rotate(360deg);
		}
	}
</style>
